package com.wxweven.disruptors;import com.lmax.disruptor.EventTranslator;import java.util.Random;/** * Created with IntelliJ IDEA. * Project: test-jar * Author: Kevin * Date: 16/5/24 * Time: 下午8:05 */public class TestObjectTranslator implements EventTranslator<TestObject> {    private Random random = new Random();    @Override    public void translateTo(TestObject event, long sequence) {        this.generateTestObject(event);    }    private TestObject generateTestObject(TestObject testObject) {        testObject.setValue(random.nextLong() % 10000);        return testObject;    }}